import React from "react";
import classes from './MenuToggle.module.css'


const MenuToggle = props => {
    const cls = [
        'fa',
        classes.MenuToggle
    ]

    if (props.isOpen) {
        cls.push('fa-times')
        cls.push(classes.MenuToggle.open)
    } else {
        cls.push('fa-bars')
    }


    return (
        <i
            className={cls.join(' ')}
            onClick={props.onToggle}
        />
    )
}
export default MenuToggle
